import React, { Component } from 'react';
import { connect } from 'react-redux';
import { addCat } from '../action';
import { TextField, Button, Fab } from '@material-ui/core';
import AddIcon from '@material-ui/icons/Add';

class CatForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      breed: '',
      desc: '',
      add: false,
      error: {
        name: 'Name must not be empty',
        breed: 'Breed must not be empty',
        desc: 'Description must not be empty',
      },
    };

    this.onChange = this.onChange.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
    this.validateForm = this.validateForm.bind(this);
    this.toggleAdd = this.toggleAdd.bind(this);
  }

  toggleAdd(e) {
    // e.preventDefault();
    this.setState({add: true});
  }

  validateForm(err) {
    let valid = true;
    Object.values(err).forEach((val) => 
      val.length > 0 && (valid = false)
    );
    return valid;
  }

  onChange(e) {
    e.preventDefault();
    const {name,value} = e.target;
    let error = this.state.error;
    switch (name) {
      case 'name':
        error.name = value.length < 1 ?
        'Name must not be empty' : 
        ''
        break;
      case 'breed':
        error.breed = value.length < 1 ?
        'Breed must not be empty' :
        ''
        break;
      case 'desc':
        error.desc = value.length < 1 ?
        'Description must not be empty' :
        ''
        break;
      default:
        break;
    }
    this.setState({error, [name]:value});
  }

  onSubmit(e) {
    e.preventDefault();
    if(this.validateForm(this.state.error)) {
      const cat = {
        name: this.state.name,
        breed: this.state.breed,
        desc: this.state.desc
      }
      this.props.addCat(cat);
      this.setState({add: false});
    }
  }

  render() {
    const view = this.state.add ? 
      <div className="card-content-add">
        <form onSubmit={this.onSubmit}>
          <TextField 
            id="name"
            label="Cat Name"
            name="name"
            error={this.state.error.name}
            required="true"
            value={this.state.name}
            onChange={this.onChange} 
          />
          <TextField 
            id="breed"
            label="Cat Breed"
            name="breed"
            error={this.state.error.breed}
            required="true"
            value={this.state.breed}
            onChange={this.onChange} 
          />
          <br />
          <TextField 
            id="desc"
            label="Description"
            name="desc"
            error={this.state.error.desc}
            required="true"
            value={this.state.desc}
            onChange={this.onChange} 
            style={{width: 210}}
          />
          <Button variant="contained" color="primary" type="submit" id="add-button">Add</Button>
        </form>
      </div> :
      <Button variant="contained" color="primary" type="button" id="add-toggle-button" onClick={this.toggleAdd}><AddIcon /></Button>
    return(
      <div className="cat-card-add">
        {view}
      </div>
    );
  }
}

export default connect(null,{ addCat })(CatForm);
